package loopingInJava;

public class ForLoopExpl {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// multiple table of 2
		//Range Based loop
//		Multiplication table of 2
		for(int i=1;i<=10;i++) {
			System.out.println(i*12+"\t"+i*13+"\t"+i*14+"\t"+i*15+"\t"+i*16+"\t"+i*17+"\t"+i*18+"\t"+i*19);
		}

	}

}
